	/**
	 * NTV SEARCH FUNCTION
	 */
	function ntv_search(srchInputElm){
		var keyword = srchInputElm.val().trim().toLowerCase().replace(/\s/g,'+');
		if(keyword==''){
			srchInputElm.css({'background':'#FF9','color':'#444'}).focus()
		}else{
			var srch_type = $('input[name="srch_type"]:checked').val();
			if(srch_type=='google'){
				var URL = 'http://www.ntvbd.com/search/google/?q='+keyword+'&cx='+encodeURIComponent('partner-pub-2048928514082800:7777012178')+'&cof='+encodeURIComponent('FORID:10')+'&ie=UTF-8&sa=Search&nojs=1';
				window.location.href = URL;
			}else if(srch_type=='youtube'){
				var URL = 'http://www.ntvbd.com/search/youtube/?q='+keyword+'';
				window.location.href = URL;
			}
			else{
				var URL = 'http://www.ntvbd.com/search/?q='+keyword;					
				window.location.href = URL;
			}
		}
	}
	
